package com.basicprogram;

public class test1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 19, b = 52, c;// a=45,b=89,c;

		int x = a % 10;// int x=a%10;

		int y = a / 10;// int y=a/10;

		int u = b % 10;// in u=b%10;

		int v = b / 10;// int v=b/10;

		c = (x * 1000) + (y * 100) + (u * 10) + (v * 1);// c=(x*1000)+(y*100)+(u*10)+(v*1);

		System.out.println(c);// System.out.println(c);

	}

}
