package com.basicprogram4muthusir;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
int given=5;
do {
int n=1;
int fact=1;
while(n<=given)
{
	fact=fact*n
			;
	n=n+1;
	
}
System.out.println(fact);
given=given-1;

	}while(given>0);
		
	}

}


